mapper.select:
select id, order_id, product_id, price, number_of_products, total_money, color
from order_details
where order_id = #{_parameter,jdbcType=INTEGER}

mapper.update:
update order_details
order_id = #{orderId}, product_id = #{productId}, price = #{price}, number_of_products = #{numberOfProducts}, total_money = #{totalMoney}, color = #{color},
where id = #{id,jdbcType=INTEGER}
